// src/styles/GlobalStyles.js
import { createGlobalStyle } from 'styled-components';

const GlobalStyle = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap');

  body {
    font-family: 'Raleway', sans-serif;
    color: ${({ theme }) => theme.colors.text};
    background-color: ${({ theme }) => theme.colors.background};
    margin: 0;
    padding: 0;
  }

  h1, h2, h3, h4 {
    color: ${({ theme }) => theme.colors.title};
  }

  input, textarea, button, select, option {
    font-family: 'Raleway', sans-serif;
  }

  input {
    background-color: ${({ theme }) => theme.colors.input};
  }

  button, a.button {
    background-color: ${({ theme }) => theme.colors.button}; /* Cor de fundo global para botões */
    color: #fff;
    border: none;
    border-radius: 8px; /* Borda arredondada suave */
    padding: 12px 30px;
    cursor: pointer;
    font-weight: bold;
    font-family: 'Raleway', sans-serif;
    text-decoration: none;
    position: relative;
    overflow: hidden; /* Garante que os efeitos de sombra e gradiente não ultrapassem o botão */
    transition: transform 0.3s ease, box-shadow 0.3s ease, background-color 0.3s ease;

    &::before {
      content: '';
      position: absolute;
      top: 50%;
      left: 50%;
      width: 300%;
      height: 300%;
      background: radial-gradient(circle, rgba(255, 255, 255, 0.15), rgba(255, 255, 255, 0) 60%);
      transition: opacity 0.3s ease;
      transform: translate(-50%, -50%) scale(0);
      opacity: 0;
      z-index: 0;
    }

    &:hover::before {
      transform: translate(-50%, -50%) scale(1);
      opacity: 1;
    }

    &:hover {
      color: ${({ theme }) => theme.colors.buttonHoverText}; /* Cor do texto no hover, se desejar */
      transform: scale(1.05);
      box-shadow: 0 8px 20px rgba(0, 0, 0, 0.25); /* Sombra suave para efeito de flutuação */
      background-color: ${({ theme }) => theme.colors.buttonHover}; /* Cor do fundo no hover */
    }
`;

export default GlobalStyle;
