// src/components/UserWidget.jsx
import React from 'react';
import { UserWidgetContainer, InfoItem } from '../styles/UserWidget.styles';

const UserWidget = ({ user }) => (
  <UserWidgetContainer>
    <h2>Informações de {user.firstName}</h2>
    <div>
      <InfoItem>
        <strong>Nome:</strong> {user.firstName}
      </InfoItem>
      <InfoItem>
        <strong>Email:</strong> {user.email}
      </InfoItem>
      <InfoItem>
        <strong>Data de Criação:</strong> {user.createdAt || 'Data não disponível'}
      </InfoItem>
    </div>
  </UserWidgetContainer>
);

export default UserWidget;
