// Navbar.js
import React from 'react';

const Navbar = ({ onAddTaskClick, onFilterChange }) => {
  return (
    <nav style={styles.navbar}>
      <div style={styles.navContent}>
        <div style={styles.filterContainer}>
          <button style={styles.navButton} onClick={() => onFilterChange('all')}>Todas</button>
          <button style={styles.navButton} onClick={() => onFilterChange('completed')}>Concluídas</button>
          <button style={styles.navButton} onClick={() => onFilterChange('pending')}>Pendentes</button>
          <button style={styles.addTaskButton} onClick={onAddTaskClick}>Adicionar Tarefa</button>
        </div>
      </div>
    </nav>
  );
};

const styles = {
  navbar: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '1rem 2rem',
    margin: '5rem auto',
    backgroundColor: '#000',  // Fundo preto para destaque
    color: '#fff',
    boxShadow: '0 2px 4px rgba(0, 0, 0, 0.5)',
    marginBottom: '2rem',  // Margem inferior para separação visual
  },
  navContent: {
    display: 'flex',
    alignItems: 'center',
  },
  navButton: {
    backgroundColor: '#222',  // Botões com fundo preto para uniformidade
    color: '#fff',
    border: '1px solid #444',  // Bordas sutis para destacar os botões
    borderRadius: '4px',
    padding: '10px 15px',
    margin: '0 5px',
    cursor: 'pointer',
    transition: 'background-color 0.3s, transform 0.2s',
    fontSize: '0.9rem',  // Fonte ajustada para um visual elegante
  },
  addTaskButton: {
    backgroundColor: '#fff',  // Botão de adicionar tarefa com fundo branco para destaque
    color: '#000',
    border: '1px solid #ddd',
    borderRadius: '4px',
    padding: '10px 15px',
    margin: '0 5px',
    cursor: 'pointer',
    transition: 'background-color 0.3s, color 0.3s, transform 0.2s',
    fontSize: '0.9rem',
  },
  filterContainer: {
    display: 'flex',
  },
};

// Adicionando efeitos de hover para os botões
styles.navButton[':hover'] = {
  backgroundColor: '#333',
  transform: 'scale(1.05)',  // Leve aumento ao passar o mouse
};

styles.addTaskButton[':hover'] = {
  backgroundColor: '#f0f0f0',
  color: '#000',
  transform: 'scale(1.05)',
};

export default Navbar;
