import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const CreateTaskModal = ({ isOpen, onClose, onCreate, token, task }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (isOpen) {
      // Se h치 uma tarefa atual, preencha os campos; caso contr치rio, limpe-os
      if (task) {
        setTitle(task.taskTitle || '');
        setDescription(task.taskDescription || '');
      } else {
        setTitle('');
        setDescription('');
      }
    } else {
      // Limpa os campos quando o modal fecha
      setTitle('');
      setDescription('');
    }
  }, [isOpen, task]);

  const handleSubmit = async () => {
    if (isSubmitting) return;

    setIsSubmitting(true);
    setError('');

    try {
      const method = task ? 'PUT' : 'POST';
      const url = task ? `http://localhost:5000/api/manager/tasks/${task.id}` : 'http://localhost:5000/api/manager/tasks';
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ title, description }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Erro ao criar tarefa');
      }

      const result = await response.json();
      onCreate(result);
      onClose();
    } catch (err) {
      setError(err.message);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleClose = () => {
    if (!isSubmitting) {
      onClose(); // Fecha o modal e limpa os campos
    }
  };

  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent>
        <CloseButton onClick={handleClose} aria-label="Close modal">칑</CloseButton>
        <h2>{task ? 'Editar Tarefa' : 'Criar Nova Tarefa'}</h2>
        {error && <Error>{error}</Error>}
        <Form>
          <Label htmlFor="title">T칤tulo</Label>
          <Input
            id="title"
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <Label htmlFor="description">Descri칞칚o</Label>
          <Textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
          <ButtonContainer>
            <Button onClick={handleSubmit} disabled={isSubmitting}>
              {isSubmitting ? 'Salvando...' : 'Salvar Tarefa'}
            </Button>
            <CancelButton onClick={handleClose} disabled={isSubmitting}>
              Cancelar
            </CancelButton>
          </ButtonContainer>
        </Form>
      </ModalContent>
    </ModalOverlay>
  );
};

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ModalContent = styled.div`
  background: white;
  padding: 2rem;
  border-radius: 8px;
  position: relative;
  width: 500px;
  max-width: 90%;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
`;

const CloseButton = styled.button`
  position: absolute;
  top: 10px;
  right: 10px;
  background: transparent;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
`;

const Form = styled.div`
  display: flex;
  flex-direction: column;
`;

const Label = styled.label`
  margin: 0.5rem 0;
`;

const Input = styled.input`
  padding: 0.5rem;
  border: 1px solid #ddd;
  border-radius: 4px;
  margin-bottom: 1rem;
`;

const Textarea = styled.textarea`
  padding: 0.5rem;
  border: 1px solid #ddd;
  border-radius: 4px;
  margin-bottom: 1rem;
  min-height: 100px;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 1rem;
`;

const Button = styled.button`
  padding: 0.5rem;
  border: none;
  border-radius: 4px;
  background: #007aff;
  color: white;
  font-size: 1rem;
  cursor: pointer;
  transition: background 0.3s;

  &:hover {
    background: #0051a8;
  }

  &:disabled {
    background: #aaa;
    cursor: not-allowed;
  }
`;

const CancelButton = styled(Button)`
  background: #aaa;
  color: #333;

  &:hover {
    background: #888;
  }
`;

const Error = styled.div`
  color: red;
  margin-bottom: 1rem;
  font-size: 0.875rem;
`;

export default CreateTaskModal;
