// Header.js
import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import { FaBars } from 'react-icons/fa';
import { useAuth } from '../../hooks/useAuth'; // Certifique-se de que o caminho esteja correto
import CreateTaskModal from '../CreateTaskModal';
import UserMenu from './UserMenu';
import TaskMenu from './TaskMenu';

const Header = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isCreateTaskModalOpen, setIsCreateTaskModalOpen] = useState(false);
  const { token, name, login, register } = useAuth(); // Utilize o hook useAuth aqui

  useEffect(() => {
    if (token) {
      try {
        // Decodifica o token para obter o nome de usuário
        const decodedToken = jwtDecode(token);
        setName(decodedToken.username || 'Usuário');
      } catch (error) {
        console.error('Erro ao decodificar o token', error);
      }
    }
  }, [token, location]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuthenticated(false);
    navigate('/login');
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const openCreateTaskModal = () => {
    setIsCreateTaskModalOpen(true);
  };

  const closeCreateTaskModal = () => {
    setIsCreateTaskModalOpen(false);
  };

  const handleCreateTask = (task) => {
    console.log('Nova tarefa criada:', task);
  };

  const handleViewTasks = () => {
    navigate('/user-tasks');
  };

  const isOnLoginPage = location.pathname === '/login';
  const isAuthenticated = !!token; // Verifica se o token está presente

  return (
    <>
      <Container>
        <StyledLink to={isAuthenticated ? `/tasks/${encodeURIComponent(name)}` : '/'}>
          <Logo src="https://firebasestorage.googleapis.com/v0/b/connectrip-10205.appspot.com/o/task%2Fconnecter-logo-preview.png?alt=media&token=5891cb1a-9b80-4908-ba62-ebf81bbeb46a" alt="Connecter Logo" />
        </StyledLink>
        {isAuthenticated ? (
          <MenuContainer>
            <MenuIcon onClick={toggleMenu}>
              <FaBars />
            </MenuIcon>
            <UserMenu name={name} onLogout={handleLogout} />
            {isMenuOpen && (
              <TaskMenu 
                onOpenCreateTaskModal={openCreateTaskModal} 
                onViewTasks={handleViewTasks} 
              />
            )}
          </MenuContainer>
        ) : (
          !isOnLoginPage && (
            <LoginButton to="/login">Fazer Login</LoginButton>
          )
        )}
      </Container>
      <CreateTaskModal 
        isOpen={isCreateTaskModalOpen} 
        onClose={closeCreateTaskModal} 
        onCreate={handleCreateTask} 
        token={token} 
      />
    </>
  );
};

const Container = styled.header`
  display: flex;
  align-items: center;
  justify-content: space-between;
  background: rgba(0, 0, 0, 0.6); /* Transparente com fundo preto */
  height: 80px;
  padding: 0 20px; /* Padding maior para alinhamento melhor */
  margin: 0;
  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.3); /* Sombra mais suave */
  width: 100%;
  top: 0;
  left: 0;
  z-index: 1000;
  position: fixed;
  backdrop-filter: blur(8px); /* Blur mais intenso */
`;

const StyledLink = styled(Link)`
  display: flex;
  align-items: center;
  text-decoration: none;
  height: 100%;
`;

const Logo = styled.img`
height: 30px;
width: auto;
cursor: pointer;
`;

const MenuContainer = styled.div`
  display: flex;
  align-items: center;
  position: relative;
  color: #fff;
  margin-right: 2rem;
`;

const MenuIcon = styled.div`
  font-size: 1.8rem; /* Ícone maior para maior visibilidade */
  cursor: pointer;
  margin-right: 1.5rem; /* Espaçamento maior entre ícones e outros elementos */
  color: #fff; /* Cor branca para o ícone */
`;

const LoginButton = styled(Link)`
  color: #fff;
  text-decoration: none;
  background-color: #000;
  margin-right: 2rem;
  padding: 0.6rem 1.2rem; /* Maior padding para botão mais visível */
  border: 3px solid #fff; /* Borda mais espessa */
  border-radius: 6px; /* Bordas mais arredondadas */
  cursor: pointer;
  font-size: 1.1rem; /* Texto um pouco maior para melhor leitura */

  &:hover {
    background-color: #fff;
    color: #000;
  }
`;

export default Header;
