// components/Manager/TaskManager.js
import React from 'react';
import Navbar from './Navbar';
import TaskModal from './TaskModal';
import DropdownMenu from './DropdownMenu';
import useFetchTasks from '../hooks/Manager Hooks/useFetchTasks';
import useTaskModal from '/hooks/Manager Hooks/useTaskModal';

const TaskManager = () => {
  const { tasks, loading, setFilter, saveTask, completeTask, deleteTask } = useFetchTasks();
  const { modalOpen, currentTask, openModal, closeModal } = useTaskModal();

  // Função para abrir o modal de adicionar tarefa
  const handleAddTaskClick = () => {
    openModal(null);
  };

  return (
    <div style={styles.container}>
      <Navbar onAddTaskClick={handleAddTaskClick} onFilterChange={setFilter} />
      <div style={styles.taskWrapper}>
        <div style={styles.taskGrid}>
          {loading ? (
            <div style={styles.loadingMessage}>Carregando tarefas...</div>
          ) : tasks.length > 0 ? (
            tasks.map(task => (
              <div 
                key={task.id} 
                style={styles.taskItem}
              >
                <DropdownMenu
                  onEdit={() => openModal(task)}
                  onDelete={() => deleteTask(task.id)}
                  onComplete={() => completeTask(task.id)}
                  isCompleted={task.taskStatus}
                />
                <div style={styles.taskContent}>
                  <h3 style={styles.taskTitle}>Título: {task.taskTitle}</h3>
                  <p style={styles.taskDescription}>Descrição: {task.taskDescription}</p>
                  <p style={styles.taskCreatedAt}>Criado em: {new Date(task.createdAt).toLocaleString()}</p>
                  <p style={styles.taskStatus}>
                    Status: {task.taskStatus ? 'Concluída' : 'Pendente'}
                  </p>
                </div>
              </div>
            ))
          ) : (
            <div style={styles.noTasksMessage}>Não há tarefas para exibir.</div>
          )}
        </div>
      </div>
      <TaskModal 
        isOpen={modalOpen} 
        onClose={closeModal} 
        onSave={saveTask} 
        task={currentTask} 
      />
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    height: '100vh',
    backgroundColor: '#000',
    color: '#fff',
    overflow: 'hidden',
  },
  taskWrapper: {
    flex: 1,
    overflowY: 'auto',
    padding: '20px',
  },
  taskGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
    gap: '20px',
  },
  taskItem: {
    backgroundColor: '#1a1a1a',
    padding: '20px',
    borderRadius: '12px',
    boxShadow: '0 6px 15px rgba(255, 255, 255, 0.1)', // Sombra branca suave
    position: 'relative',
    display: 'flex',
    flexDirection: 'column',
    transition: 'transform 0.3s ease, box-shadow 0.3s ease',
    cursor: 'pointer',
    border: '1px solid #333',
  },
  loadingMessage: {
    gridColumn: '1 / -1',
    textAlign: 'center',
    fontSize: '18px',
    color: '#888',
  },
  taskContent: {
    display: 'flex',
    flexDirection: 'column',
  },
  taskTitle: {
    fontSize: '20px',
    fontWeight: 'bold',
    marginBottom: '8px',
    color: '#f5f5f5',
  },
  taskDescription: {
    fontSize: '15px',
    lineHeight: '1.5',
    color: '#d1d1d1',
    marginBottom: '10px',
  },
  taskCreatedAt: {
    fontSize: '13px',
    color: '#999',
    marginBottom: '8px',
  },
  taskStatus: {
    fontSize: '14px',
    fontWeight: 'bold',
    color: '#4caf50',
  },
  noTasksMessage: {
    gridColumn: '1 / -1',
    textAlign: 'center',
    fontSize: '18px',
    color: '#888',
  },
};

export default TaskManager;
