// Intro.js
import React, { useState } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const Intro = () => {
  const [imageLoaded, setImageLoaded] = useState(false);
  const [imageError, setImageError] = useState(false);

  // Função para tratar o carregamento da imagem
  const handleImageLoad = () => {
    setImageLoaded(true);
  };

  // Função para tratar erros no carregamento da imagem
  const handleImageError = () => {
    setImageError(true);
  };

  // Condicional para renderização
  if (imageError) {
    return (
      <IntroContainer>
        <ErrorMessage>Erro ao carregar a imagem.</ErrorMessage>
      </IntroContainer>
    );
  }

  if (!imageLoaded) {
    return (
      <IntroContainer>
        <LoadingMessage>Carregando...</LoadingMessage>
      </IntroContainer>
    );
  }

  return (
    <IntroContainer>
      <Logo
        src="/connecter-intro-preview.png"
        alt="Connecter Intro"
        onLoad={handleImageLoad}
        onError={handleImageError}
      />
      <Title>Connecter</Title>
      <LoginButton to="/login">Iniciar Sessão</LoginButton>
      <IntroText>
        O melhor lugar para gerenciar suas tarefas e muito mais.
      </IntroText>
    </IntroContainer>
  );
};

const IntroContainer = styled.div`
  text-align: center;
  margin: -50px;
`;

const Logo = styled.img`
  width: 450px; /* Ajuste o tamanho conforme necessário */
  margin-bottom: 0.8rem; /* Espaço entre o título e o texto */
`;

const Title = styled.h1`
  font-size: 6rem;
  color: #000;
  font-weight: bold;
  margin: -50px;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
  margin-bottom: 2rem; /* Espaço entre o título e o texto */
`;

const IntroText = styled.p`
  font-size: 2rem;
  color: #000;
  font-weight: bold;
  margin: 50px auto;
  max-width: 500px; /* Limita a largura do texto */
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
  margin-bottom: 8rem; /* Espaço entre o texto e o botão */
`;

const LoginButton = styled(Link)`
  background-color: #000; /* Cor do botão */
  color: #fff;
  border: none;
  padding: 0.75rem 2rem;
  border-radius: 20px;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.3s;
  text-decoration: none;

  &:hover {
    color: #000;
    background-color: gray; /* Cor do botão ao passar o mouse */
  }
`;

const LoadingMessage = styled.p`
  font-size: 2rem;
  color: #000;
  font-weight: bold;
  margin: 50px auto;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
`;

const ErrorMessage = styled.p`
  font-size: 2rem;
  color: red;
  font-weight: bold;
  margin: 50px auto;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
`;

export default Intro;
