import { useState, useCallback } from 'react';

/**
 * Hook para gerenciar o estado do modal de tarefas.
 * Inclui funções para abrir, fechar e definir a tarefa atual.
 */
const useTaskModal = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [currentTask, setCurrentTask] = useState(null);

  // Abre o modal e define a tarefa atual
  const openModal = useCallback((task) => {
    setCurrentTask(task);
    setModalOpen(true);
  }, []);

  // Fecha o modal e limpa a tarefa atual
  const closeModal = useCallback(() => {
    setModalOpen(false);
    setCurrentTask(null);
  }, []);

  return { modalOpen, currentTask, openModal, closeModal };
};

export default useTaskModal;
