import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import styled, { keyframes } from 'styled-components';
import { jwtDecode } from 'jwt-decode';

const capitalizeFirstLetter = (string) => {
  return string.charAt(0).toUpperCase() + string.slice(1);
};

const formatDate = (dateString) => {
  const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
  return new Date(dateString).toLocaleDateString(undefined, options);
};

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const TasksPage = () => {
  const { username } = useParams();
  const [tasks, setTasks] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState({ firstName: '', email: '' });
  const [selectedTask, setSelectedTask] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    console.log('Token:', token);

    if (token) {
      setIsAuthenticated(true);

      try {
        const decodedToken = jwtDecode(token);
        const fullName = decodedToken.name || 'Nome não disponível';
        const firstName = capitalizeFirstLetter(fullName.split(' ')[0]);

        setUser({
          firstName,
          email: decodedToken.email || 'Email não disponível',
        });

        if (username !== firstName) {
          navigate(`/tasks/${encodeURIComponent(firstName)}`);
        }
      } catch (error) {
        console.error('Erro ao decodificar o token:', error);
      }
    } else {
      const fetchUserInfo = async () => {
        try {
          const response = await fetch(
            `http://localhost:5000/api/users/user/${encodeURIComponent(username)}`
          );
          if (response.ok) {
            const data = await response.json();
            setUser({
              firstName: capitalizeFirstLetter(data.name),
              email: data.email,
            });
          } else {
            console.error(
              'Erro ao carregar informações do usuário:',
              response.statusText
            );
          }
        } catch (error) {
          console.error('Erro ao carregar informações do usuário:', error);
        }
      };

      fetchUserInfo();
    }

    const fetchTasks = async () => {
      try {
        const response = await fetch(
          `http://localhost:5000/api/tasks`, // Corrigida a rota aqui
          {
            headers: {
              Authorization: token ? `Bearer ${token}` : '',
            },
          }
        );

        if (response.ok) {
          const data = await response.json();
          setTasks(data);
        } else {
          console.error('Erro ao carregar tarefas:', response.statusText);
        }
      } catch (error) {
        console.error('Erro ao carregar tarefas:', error);
      }
    };

    fetchTasks();
  }, [navigate, username]);

  const handleViewTask = (task) => {
    setSelectedTask(task);
  };

  const handleCloseModal = () => {
    setSelectedTask(null);
  };

  return (
    <Container>
      <WidgetsContainer>
        <UserWidget>
          <h2>Informações de {user.firstName}</h2>
          <UserInfo>
            <InfoItem>
              <strong>Nome:</strong> {user.firstName}
            </InfoItem>
            <InfoItem>
              <strong>Email:</strong> {user.email}
            </InfoItem>
          </UserInfo>
        </UserWidget>
        <TasksWidget>
          <h2>Tarefas de {user.firstName}</h2>
          <TaskGrid>
            {tasks.slice(0, 6).map((task) => (
              <TaskItem key={task.id}>
                <TaskName>{task.taskTitle}</TaskName>
                <TaskDate>{formatDate(task.createdAt)}</TaskDate>
                <ActionButton
                  onClick={() => handleViewTask(task)}
                  disabled={!isAuthenticated}
                >
                  Ver Tarefa
                </ActionButton>
              </TaskItem>
            ))}
          </TaskGrid>
        </TasksWidget>
      </WidgetsContainer>

      {selectedTask && (
        <TaskModal>
          <ModalContent>
            <h3>{selectedTask.taskTitle}</h3>
            <p><strong>Descrição:</strong> {selectedTask.taskDescription}</p>
            <p>
              <strong>Status:</strong> {selectedTask.taskStatus ? 'Concluído' : 'Pendente'}
            </p>
            <p><strong>Data e Hora de Criação:</strong> {formatDate(selectedTask.createdAt)}</p>
            <CloseButton onClick={handleCloseModal}>Fechar</CloseButton>
          </ModalContent>
        </TaskModal>
      )}
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  justify-content: center;
  background: url('/connecter-background.jpg') no-repeat center center fixed;
  background-size: cover;
  padding: 2rem;
  min-height: 85vh;
  align-items: flex-start;
  color: #dcdcdc; /* Cor de texto mais clara para contraste */
`;

const WidgetsContainer = styled.div`
  display: flex;
  gap: 2rem;
  width: 100%;
  margin-top: 10rem;
  max-width: 1200px;
  justify-content: center;
`;

const Widget = styled.div`
  background: rgba(0, 0, 0, 0.8); /* Fundo preto semitransparente */
  border-radius: 15px;
  box-shadow: 0 8px 16px rgba(255, 255, 255, 0.2), inset 0 -3px 6px rgba(255, 255, 255, 0.1);
  padding: 2rem;
  width: 100%;
  box-sizing: border-box;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  animation: ${fadeIn} 0.6s ease-in-out;

  &:hover {
    transform: translateY(-8px);
    box-shadow: 0 12px 24px rgba(255, 255, 255, 0.3), inset 0 -4px 8px rgba(255, 255, 255, 0.2);
  }
`;

const TasksWidget = styled(Widget)`
  flex: 2;
  max-width: 750px;
  background: rgba(0, 0, 0, 0.7);

  h2 {
    color: #f70073; /* Cor rosa para o título */
    font-weight:bold;
    font-size: 1.8rem;
    margin-bottom: 1rem;
  }
`;

const UserWidget = styled(Widget)`
  flex: 1;
  max-width: 350px;
  background: rgba(0, 0, 0, 0.7);

  h2 {
    color: #f70073; /* Cor rosa para o título */
    font-weight:bold;
    font-size: 1.8rem;
    margin-bottom: 1rem;
  }
`;

const UserInfo = styled.div`
  margin-top: 1.5rem;
`;

const InfoItem = styled.div`
  margin-bottom: 0.75rem;
  font-size: 1.1rem;
  color: #f0f0f0; /* Cor do texto para maior contraste */
  font-weight: 500;
`;

const TaskGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(220px, 1fr));
  gap: 1.5rem;
`;

const TaskItem = styled.div`
  background: #000;
  border: 1px solid #f70073;
  border-radius: 12px;
  padding: 1.5rem;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  font-size: 1rem;
  transition: background-color 0.3s ease, transform 0.3s ease;
  animation: ${fadeIn} 0.6s ease-in-out;

  &:hover {
    background-color: #555;
    transform: translateY(-3px);
  }
`;

const TaskName = styled.span`
  font-size: 1.1rem;
  font-weight: 500;
  color: #fafafa; /* Cor do texto branco para melhor visibilidade */
`;

const TaskDate = styled.span`
  font-size: 0.9rem;
  color: #bbb; /* Cor mais clara para a data */
  margin-top: 0.5rem;
`;

const ActionButton = styled.button`
  background: linear-gradient(145deg, #ff4f77, #ff1a66); /* Gradiente rosa */
  color: white;
  border: none;
  border-radius: 8px;
  padding: 0.75rem 1.5rem;
  cursor: pointer;
  font-size: 1rem;
  margin-top: 1rem;
  transition: background 0.3s ease, transform 0.3s ease;

  &:hover {
    background: linear-gradient(145deg, #e0466d, #e01a5f);
    transform: scale(1.05);
  }

  &:disabled {
    background: #666;
    cursor: not-allowed;
  }
`;

const TaskModal = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.6);
  display: flex;
  justify-content: center;
  align-items: center;
  animation: ${fadeIn} 0.6s ease-in-out;
`;

const ModalContent = styled.div`
  background: #1e1e1e;
  padding: 2rem;
  border-radius: 10px;
  width: 80%;
  max-width: 500px;
  color: #dcdcdc;
  animation: ${fadeIn} 0.6s ease-in-out;
`;

const CloseButton = styled.button`
  background: #ff1a66;
  color: white;
  border: none;
  border-radius: 8px;
  padding: 0.5rem 1rem;
  cursor: pointer;
  font-size: 1rem;
  margin-top: 1rem;
  transition: background 0.3s ease;

  &:hover {
    background: #e01a5f;
  }
`;

export default TasksPage;
