// src/components/Loader.js
import React from 'react';
import styled from 'styled-components';

const Loader = () => (
  <LoaderContainer>
    <LoaderSpinner />
  </LoaderContainer>
);

const LoaderContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh; /* Ocupa toda a altura da tela */
  background: rgba(255, 255, 255, 0.8); /* Fundo branco semi-transparente */
  position: absolute;
  width: 100%;
  top: 0;
  left: 0;
  z-index: 9999; /* Coloca o loader sobre todos os outros elementos */
`;

const LoaderSpinner = styled.div`
  border: 8px solid #f3f3f3; /* Cor de fundo do spinner */
  border-top: 8px solid #3498db; /* Cor do topo do spinner */
  border-radius: 50%;
  width: 50px;
  height: 50px;
  animation: spin 1s linear infinite;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

export default Loader;
