// Intro.js
import React from 'react';
import styled, { keyframes } from 'styled-components';
import { Link } from 'react-router-dom';

// Animação de entrada para os elementos
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(-20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const Intro = () => {
  return (
    <IntroContainer>
      <BackgroundAnimation />
      <Logo
        src="https://firebasestorage.googleapis.com/v0/b/connectrip-10205.appspot.com/o/task%2FConnecter-intro.gif?alt=media&token=76402d79-bb17-45e7-a831-5a1fd29818ab"
        alt="Connecter Intro"
      />
      <Title>Connecter</Title>
      <LoginButton to="/login">Iniciar Sessão</LoginButton>
      <IntroText>O melhor lugar para gerenciar suas tarefas e muito mais.</IntroText>
    </IntroContainer>
  );
};

const IntroContainer = styled.div`
  text-align: center;
  padding: 2rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  overflow: hidden;
  position: relative; /* Para permitir animação de fundo */
  background: linear-gradient(to right, #000, #333); /* Fundo gradiente */
  animation: ${fadeIn} 1s ease-in-out;
`;

const Logo = styled.img`
  width: 350px;
  height: 350px;
  margin-bottom: -2rem;
  margin-top: 3rem;
  animation: ${fadeIn} 1.5s ease-out forwards;
  opacity: 0; /* Iniciar invisível e aparecer gradualmente */
`;

const Title = styled.h1`
  font-size: 6rem;
  color: #fff;
  font-weight: bold;
  font-family: 'Raleway', sans-serif;
  margin-bottom: 1.7rem;
  animation: ${fadeIn} 2s ease-out forwards;
  opacity: 0;
`;

const IntroText = styled.p`
  font-size: 2rem;
  color: #fff;
  font-weight: bold;
  margin: 1rem auto;
  max-width: 500px;
  font-family: 'Raleway', sans-serif;
  animation: ${fadeIn} 2.5s ease-out forwards;
  opacity: 0;
`;

const LoginButton = styled(Link)`
  background-color: #fff;
  color: #000;
  border: none;
  padding: 0.75rem 2rem;
  border-radius: 20px;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  margin-bottom: 1.7rem;
  transition: background-color 0.3s, transform 0.2s;
  text-decoration: none;
  animation: ${fadeIn} 3s ease-out forwards;
  opacity: 0;

  &:hover {
    color: #fff;
    background-color: #555;
    transform: scale(1.05); /* Aumenta ligeiramente o botão */
  }

  &:active {
    transform: scale(0.95); /* Efeito de clique */
  }
`;

const BackgroundAnimation = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: radial-gradient(circle, rgba(255,255,255,0.1), transparent 70%);
  animation: ${fadeIn} 4s ease-in-out infinite;
  z-index: -1; /* Coloca o fundo atrás dos outros elementos */
  filter: blur(10px); /* Aplica um leve desfoque */
  opacity: 0.7; /* Ajusta a opacidade para não sobrecarregar */
`;

export default Intro;
