import React, { useState } from 'react';
import DropdownMenu from './DropdownMenu';
import styled from 'styled-components';

const TaskItemContainer = styled.div`
  background-color: #1a1a1a;
  padding: 20px;
  border-radius: 12px;
  box-shadow: 0 6px 15px rgba(255, 255, 255, 0.1);
  position: relative;
  display: flex;
  flex-direction: column;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  cursor: pointer;
  border: 1px solid #333;

  &:hover {
    transform: translateY(-8px);
    box-shadow: 0 12px 25px rgba(255, 255, 255, 0.2);
  }
`;

const TaskContent = styled.div`
  display: flex;
  flex-direction: column;
`;

const TaskTitle = styled.h3`
  font-size: 20px;
  font-weight: bold;
  margin-bottom: 8px;
  color: #f5f5f5;
`;

const TaskDescription = styled.p`
  font-size: 15px;
  line-height: 1.5;
  color: #d1d1d1;
  margin-bottom: 10px;
`;

const TaskCreatedAt = styled.p`
  font-size: 13px;
  color: #999;
  margin-bottom: 8px;
`;

const TaskStatus = styled.p`
  font-size: 16px;
  font-weight: bold;
  color: ${props => (props.isCompleted ? '#4caf50' : '#f44336')};
`;

const TaskItem = ({ task, onEdit, onDelete, onComplete }) => {
  const [hoveredTask, setHoveredTask] = useState(null);

  return (
    <TaskItemContainer
      onMouseEnter={() => setHoveredTask(task.id)}
      onMouseLeave={() => setHoveredTask(null)}
    >
      <DropdownMenu
        onEdit={() => onEdit(task)}
        onDelete={() => onDelete(task.id)}
        onComplete={() => onComplete(task.id)}
        isCompleted={task.taskStatus}
      />
      <TaskContent>
        <TaskTitle>Título: {task.taskTitle}</TaskTitle>
        <TaskDescription>Descrição: {task.taskDescription}</TaskDescription>
        <TaskCreatedAt>Criado em: {new Date(task.createdAt).toLocaleString()}</TaskCreatedAt>
        <TaskStatus isCompleted={task.taskStatus}>
          Status: {task.taskStatus ? 'Concluída' : 'Pendente'}
        </TaskStatus>
      </TaskContent>
    </TaskItemContainer>
  );
};

export default TaskItem;
