import React from 'react';
import styled from 'styled-components';
import Intro from './Intro';
import { useLocation } from 'react-router-dom';
const Header = () => {
  return (
    <Container>
      <Logo src="/connecter-logo-preview.png" alt="Connecter Logo" />
      {location.pathname === '/' && <Intro />}{' '}
    </Container>
  );
};

const Container = styled.header`
  display: flex;
  align-items: center;
  background-color: #fff; /* Adiciona uma cor de fundo se necessário */
`;

const Logo = styled.img`
  width: 180px; /* Ajuste o tamanho conforme necessário */
  margin-left: 0; /* Remove qualquer margem à esquerda */
  margin-top: -50px;
`;

export default Header;
