import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { FaBars } from 'react-icons/fa';
import UserMenu from './UserMenu';
import TaskMenu from './TaskMenu';
import useHeader from '../../hooks/useHeader';

const Header = () => {
  const {
    isTaskMenuOpen,
    isUserMenuOpen,
    userInfo,
    isAuthenticated,
    toggleTaskMenu,
    toggleUserMenu,
    handleViewTasks,
    handleLogout,
    setIsTaskMenuOpen,
  } = useHeader();

  const handleToggleTaskMenu = () => {
    setIsTaskMenuOpen(!isTaskMenuOpen); // Alterna o estado do menu de tarefas
    if (isUserMenuOpen) {
      toggleUserMenu(); // Fecha o menu de usuário se estiver aberto
    }
  };

  return (
    <>
      <Container>
        <StyledLink to={isAuthenticated ? `/tasks/${encodeURIComponent(userInfo.username)}` : '/'}>
          <Logo src="https://firebasestorage.googleapis.com/v0/b/connectrip-10205.appspot.com/o/task%2Fconnecter-logo-preview.png?alt=media&token=5891cb1a-9b80-4908-ba62-ebf81bbeb46a" alt="Connecter Logo" />
        </StyledLink>
        {isAuthenticated ? (
          <MenuContainer>
            <MenuIcon onClick={handleToggleTaskMenu}>
              <FaBars />
            </MenuIcon>
            {isTaskMenuOpen && (
              <TaskMenu 
                onViewTasks={() => {
                  handleViewTasks();
                  setIsTaskMenuOpen(false); // Fecha o menu após a visualização das tarefas
                }} 
                onClose={() => setIsTaskMenuOpen(false)} 
              />
            )}
            <UserMenu
              name={userInfo.fullName}
              onLogout={handleLogout}
              onClick={() => {
                toggleUserMenu();
                if (isTaskMenuOpen) {
                  setIsTaskMenuOpen(false); // Fecha o menu de tarefas se o menu de usuário for aberto
                }
              }}
              isOpen={isUserMenuOpen}
            />
            {isUserMenuOpen && !isTaskMenuOpen && (
              <UserMenu 
                name={userInfo.fullName} 
                onLogout={handleLogout} 
              />
            )}
          </MenuContainer>
        ) : (
          <LoginButton to="/login">Fazer Login</LoginButton>
        )}
      </Container>
    </>
  );
};

// Styled components
const Container = styled.header`/* seu estilo aqui */`;
const StyledLink = styled(Link)`/* seu estilo aqui */`;
const Logo = styled.img`/* seu estilo aqui */`;
const MenuContainer = styled.div`/* seu estilo aqui */`;
const MenuIcon = styled.div`/* seu estilo aqui */`;
const LoginButton = styled(Link)`/* seu estilo aqui */`;

export default Header;
