import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode'; // Certifique-se de que jwt-decode está instalado

export const useAuth = () => {
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const login = async (email, password) => {
    try {
      const response = await fetch('http://localhost:5000/api/users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        throw new Error('Erro ao fazer login');
      }

      const data = await response.json();
      localStorage.setItem('token', data.token);

      // Decodifica o token para obter o nome de usuário
      const decodedToken = jwtDecode(data.token);
      console.log('Decoded Token:', decodedToken); // Adiciona um log para verificar o token decodificado

      const userName = decodedToken.username || 'Usuário'; // Use o campo correto aqui
      console.log('Username:', userName); // Adiciona um log para verificar o nome de usuário

      navigate(`/tasks/${encodeURIComponent(userName)}`);
    } catch (err) {
      setError(err.message);
      console.error('Erro ao fazer login:', err);
    }
  };

  const register = async (username, fullName, email, password) => {
    try {
      const response = await fetch('http://localhost:5000/api/users/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, fullName, email, password }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Erro ao registrar');
      }

      const data = await response.json();
      localStorage.setItem('token', data.token);
      navigate(`/tasks/${encodeURIComponent(username)}`);
    } catch (err) {
      setError(err.message);
      console.error('Erro ao registrar:', err);
    }
  };

  return { login, register, error };
};
