// Header.js
import React from 'react';
import styled from 'styled-components';
import { useLocation } from 'react-router-dom';
import Intro from './Intro'; // Importe o componente Intro

const Header = () => {
  const location = useLocation();

  return (
    <Container>
      {location.pathname === '/' && <Intro />} {/* Renderiza a introdução somente na rota '/' */}
      <Logo src="/connecter-logo-preview.png" alt="Connecter Logo" />
    </Container>
  );
};

const Container = styled.header`
  display: flex;
  flex-direction: column; /* Organiza a introdução e o logo verticalmente */
  align-items: center;
  background-color: #fff;
`;

const Logo = styled.img`
  width: 180px; /* Ajuste o tamanho conforme necessário */
  margin-left: 0;
  margin-top: -50px;
`;

export default Header;
