import React, { useState, useEffect } from 'react';
import { useAuth } from '../Context/AuthContext';
import Navbar from '../components/Manager/Navbar';
import TaskModal from '../components/Manager//TaskModal';
import TaskList from '../components/Manager/TaskList';
import LoadingMessage from '../components/Manager/LoadingMessage';
import NoTasksMessage from '../components/Manager/NoTasksMessage';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  background-color: #000;
  color: #fff;
  overflow: hidden;
`;

const TaskWrapper = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 20px;
`;

const TaskManager = () => {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState('all');
  const [modalOpen, setModalOpen] = useState(false);
  const [currentTask, setCurrentTask] = useState(null);
  const [loading, setLoading] = useState(true);
  const { token, userInfo } = useAuth();

  const fetchTasks = async () => {
    if (!token || !userInfo?.username) return;
    setLoading(true);
    try {
      const response = await fetch(`https://connecter-app-production.up.railway.app/api/manager/tasks/${userInfo.username}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!response.ok) throw new Error('Erro ao buscar tarefas');
      const data = await response.json();
      setTasks(data);
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, [token, userInfo?.username]);

  const handleAddTaskClick = () => {
    setCurrentTask(null);
    setModalOpen(true);
  };

  const handleSaveTask = async (task) => {
    try {
      const method = currentTask ? 'PUT' : 'POST';
      const url = currentTask
        ? `https://connecter-app-production.up.railway.app/api/manager/tasks/${currentTask.id}`
        : 'https://connecter-app-production.up.railway.app/api/manager/tasks';
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(task),
      });

      if (!response.ok) throw new Error('Erro ao salvar tarefa');
      await response.json();

      fetchTasks();
      setModalOpen(false);
      setCurrentTask(null);
    } catch (error) {
      console.error(error);
    }
  };

  const handleCompleteTask = async (taskId) => {
    try {
      const response = await fetch(`https://connecter-app-production.up.railway.app/api/manager/tasks/${taskId}/complete`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) throw new Error('Erro ao marcar tarefa como concluída');
      await response.json();

      fetchTasks();
    } catch (error) {
      console.error(error);
    }
  };

  const handleDeleteTask = async (taskId) => {
    try {
      const response = await fetch(`https://connecter-app-production.up.railway.app/api/manager/tasks/${taskId}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) throw new Error('Erro ao excluir tarefa');
      fetchTasks();
    } catch (error) {
      console.error(error);
    }
  };

  const filteredTasks = tasks.filter(task => {
    if (filter === 'completed') return task.taskStatus;
    if (filter === 'pending') return !task.taskStatus;
    return true;
  });

  return (
    <Container>
      <Navbar onAddTaskClick={handleAddTaskClick} onFilterChange={setFilter} />
      <TaskWrapper>
        {loading ? (
          <LoadingMessage />
        ) : filteredTasks.length > 0 ? (
          <TaskList 
            tasks={filteredTasks} 
            onEdit={(task) => {
              setCurrentTask(task);
              setModalOpen(true);
            }}
            onDelete={handleDeleteTask}
            onComplete={handleCompleteTask}
          />
        ) : (
          <NoTasksMessage />
        )}
      </TaskWrapper>
      <TaskModal 
        isOpen={modalOpen} 
        onClose={() => setModalOpen(false)} 
        onSave={handleSaveTask} 
        task={currentTask} 
      />
    </Container>
  );
};

export default TaskManager;
