import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Link, useNavigate } from 'react-router-dom';
import { FaArrowRight } from 'react-icons/fa';
import { useAuth } from '../hooks/useAuth';
import { useInput } from '../hooks/useInput';

const LoginPage = () => {
  const { login, error, verificAuth, username } = useAuth(); // Supondo que user contém o username
  const { value: email, onChange: handleEmailChange } = useInput('');
  const { value: password, onChange: handlePasswordChange } = useInput('');
  const [showPassword, setShowPassword] = useState(false);
  const [loadingStartTime, setLoadingStartTime] = useState(null);
  const [minLoadingDuration] = useState(5000);
  const navigate = useNavigate();

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const handleShowPassword = () => {
    if (validateEmail(email)) {
      setShowPassword(true);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoadingStartTime(Date.now());

    try {
      await login(email, password);

      const elapsedTime = Date.now() - loadingStartTime;
      const remainingTime = Math.max(minLoadingDuration - elapsedTime, 0);

      setTimeout(() => {
        if (verificAuth && username) {
          navigate(`/tasks/${username}`); // Redirecionar para a página de tarefas com o username
        }
      }, remainingTime);
    } catch (err) {
      console.error('Erro ao fazer login:', err);
    }
  };

  useEffect(() => {
    if (verificAuth && username) {
      navigate(`/tasks/${user.username}`); // Navegar para a página de tarefas se já autenticado
    }
  }, [verificAuth, username, navigate]);

  return (
    <Container>
      <Form onSubmit={handleSubmit}>
        <Logo
          src="https://firebasestorage.googleapis.com/v0/b/connectrip-10205.appspot.com/o/task%2FConnecter-form-preview.png?alt=media&token=da607aba-6727-4eee-a65a-968c88455272"
          alt="Connecter Intro"
        />
        <Title>Faça login com seu ID Connecter</Title>
        <EmailContainer>
          <Input
            type="email"
            placeholder="E-mail"
            value={email}
            onChange={handleEmailChange}
            autoFocus
          />
          {!showPassword && (
            <Button
              onClick={handleShowPassword}
              disabled={!validateEmail(email)}
              emptyEmail={!email}
            >
              <FaArrowRight />
            </Button>
          )}
        </EmailContainer>
        <PasswordContainer show={showPassword}>
          <Input
            type="password"
            placeholder="Senha"
            value={password}
            onChange={handlePasswordChange}
          />
          <Button type="submit">
            <FaArrowRight />
          </Button>
        </PasswordContainer>
        <RegisterLink to="/register">Criar um ID Connecter</RegisterLink>
        {error && <ErrorMessage>{error}</ErrorMessage>}
      </Form>
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 60vh;
  margin-top: 9rem;
`;

const Form = styled.form`
  padding: 2rem;
  max-width: 560px;
  width: 100%;
  text-align: center;
  background: white;
  border-radius: 30px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.4);
`;

const Logo = styled.img`
  width: 200px;
  height: 190px;
  margin-bottom: -1rem;
`;

const Title = styled.h2`
  margin-bottom: 2rem;
  font-size: 1.5rem;
  font-weight: 500;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
`;

const EmailContainer = styled.div`
  position: relative;
  width: 100%;
  margin-bottom: 1rem;
`;

const PasswordContainer = styled.div`
  position: relative;
  width: 100%;
  opacity: ${(props) => (props.show ? 1 : 0)};
  transform: ${(props) => (props.show ? 'scale(1)' : 'scale(0.95)')};
  transition: opacity 0.5s ease, transform 0.5s ease;
`;

const Input = styled.input`
  width: 70%;
  padding: 1.2rem;
  padding-right: 3.5rem;
  border: 1px solid #a4a4ad;
  border-radius: 12px;
  font-size: 1rem;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
  transition: border-color 0.3s, box-shadow 0.3s;

  &:focus {
    border-color: #007aff;
    box-shadow: 0 0 0 2px rgba(0, 122, 255, 0.2);
    outline: none;
  }
`;

const Button = styled.button`
  position: absolute;
  top: 50%;
  right: 3.6rem;
  transform: translateY(-50%);
  width: 2.2rem;
  height: 2.2rem;
  background: ${(props) =>
    props.emptyEmail ? '#d0d0d5' : '#007aff'};
  border: none;
  color: #ffffff;
  border-radius: 50%;
  font-size: 1rem;
  font-weight: bold;
  cursor: ${(props) => (props.emptyEmail ? 'default' : 'pointer')};
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: center;

  &:hover {
    background: ${(props) =>
      props.emptyEmail ? '#d0d0d5' : '#0051a8'};
  }

  &:disabled {
    cursor: not-allowed;
    opacity: 0.5;
  }
`;

const RegisterLink = styled(Link)`
  display: block;
  margin-top: 1.5rem;
  color: #007aff;
  font-size: 1rem;
  text-decoration: none;
`;

const ErrorMessage = styled.div`
  color: red;
  margin-top: 1rem;
`;

export default LoginPage;
